require 'set'
# @param {Integer[]} nums
# @return {Integer[][]}
def three_sum(nums)
    found = Set.new
    n = nums.length - 1
    nums = nums.sort
    nums.each_with_index do |el, i|
        next if el == nums[i-1] && i > 0
        triplets = two_sum(nums, i+1, n, el)
        triplets.each do |triplet|
            next if found.include? triplet
            found << triplet
        end
    end
    found.to_a
end


def two_sum(nums, from, to, target)
    results = Set.new
    seen = Hash.new{|h, k| h[k] = -1}

    from.upto(to) do |j|
        num = nums[j]
        complement = -target - num
        if seen[complement] > -1
            results << [target, num, complement].sort
        else
            seen[num] = j
        end
    end

    results.to_a
end

puts three_sum([-4,-2,-2,-2,0,1,2,2,2,3,3,4,4,6,6]) == [[-4,2,2],[-4,1,3],[-4,0,4],[-4,-2,6],[-2,0,2],[-2,-2,4]]
puts three_sum([-1, 0, 1, 2, -1, -4]) == [[-1, 0, 1],[-1, -1, 2]]
